"use strict";class ToTop extends HTMLElement{static TAG='to-top';showThreshold;goalTopOffset;showClass;scrollBehavior;constructor(...args){super(...args)}connectedCallback(){this.style.display='block';this.style.overflow='hidden';this.showThreshold=this.getAttribute('threshold')??150;this.goalTopOffset=this.getAttribute('goal')??0;this.showClass=this.getAttribute('show-class')??'show';this.scrollBehavior=this.getAttribute('behavior')??'smooth';this.addEventListener('click',this.onClick);window.addEventListener('scroll',this.onPageScroll.bind(this),{passive:true})}disconnectedCallback(){this.removeEventListener('click',this.onClick);window.removeEventListener('scroll',this.onPageScroll.bind(this),{passive:true})}goToTop(){window.scrollTo({top:this.goalTopOffset,left:0,behavior:this.scrollBehavior})}handleAppearance(){this.classList.toggle(this.showClass,window.scrollY>this.showThreshold)}onClick(ev){this.goToTop()}onPageScroll(ev){this.handleAppearance()}}customElements.define(ToTop.TAG,ToTop);